import React,{useState,useEffect} from 'react';
import { 
  View,
  Text,
  Button, 
  StyleSheet,
  Image,
  TouchableOpacity,
  FlatList
 } from 'react-native';

 import { MaterialIcons,Entypo,Feather} from '@expo/vector-icons'



import Avatar from '../assets/images/Hospital.gif';

const ProfileScreen = ({navigation}) => {

  const[data, Setdata] = useState([])
  const [loading, setLoading] = useState(true);

  // const [Name,setName] = useState("")
  // const [email,setEmail] = useState("")
  // const [code,setCode] = useState("")
  // const [phone,setPhone] = useState("")
  // const [address,setAddress] = useState("")
  // const [picture,setPicture] = useState("")


  const Boiler = async ()=>{
  await fetch('http://mconnecthealth.com:2000/get_Hospital')
    .then(res=>res.json())
    .then(results=>{
      Setdata(results)
      setLoading(false)
    }
    )
   }
useEffect(()=>{
   Boiler()
},[data])

const renderList = ((item)=>{
  return(
    <View>
    <View style={styles.HeadCard}>
             <Image 
            source={{uri:item.picture}}
            style={styles.HospitalImage}/>
             <Text style={styles.Title}>{item.hospitalname} </Text>
    </View>
    <View style={styles.bodyCard}>
      <View style={styles.body}>

             <View style={styles.detail}>
                  <MaterialIcons name="email" size={32} color="#031169" />
                    <Text style={styles.locationtext}> {item.email}</Text>
             </View>

             <View style={styles.detail}>
                   <MaterialIcons name="dialer-sip" size={32} color="#031169" />
                   <Text style={styles.locationtext}>{item.phone}</Text>
             </View>
       </View>

              <View style={{alignItems:'center'}}>
                <Text style={styles.bodytitle}> Address</Text>

               <View style={{flexDirection:'row',width:'100%'}}>
                    <Text style={styles.addresshead}>Place :</Text>
                    <Text style={styles.addresstext}>  {item.place}  </Text>
               </View> 
               <View style={{flexDirection:'row',width:'100%'}}>
                    <Text style={styles.addresshead}>Landmark :</Text>
                    <Text style={styles.addresstext}>  {item.Landmark}  </Text>
               </View> 
               <View style={{flexDirection:'row',width:'100%'}}>
                    <Text style={styles.addresshead}>District :</Text>
                    <Text style={styles.addresstext}>  {item.District}  </Text>
               </View> 
               <View style={{flexDirection:'row',width:'100%'}}>
                    <Text style={styles.addresshead}>State :</Text>
                    <Text style={styles.addresstext}>  {item.state}  </Text>
                    <Text style={styles.addresshead}>Pin Code :</Text>
                    <Text style={styles.addresstext}>  {item.pincode}  </Text>
               </View> 



              </View>

              <TouchableOpacity 
                 activeOpacity={0.95} 
                style={styles.btn} 
                  onPress={()=>navigation.navigate("EditHospital",{item})}>

                  <Feather name="edit" size={35} color="black"  style={styles.addicon}/>
              </TouchableOpacity>


             </View>

      </View>
  )})




  return (

    <View style={styles.container}>

    <FlatList
              data={data}
              renderItem={({item})=>{
                return renderList(item)
              }}
              keyExtractor={item=>item._id}
             onRefresh={()=>Boiler()}
              refreshing={loading}
              />
{/* {data && data.length > 0 && renderList(data[0])} */}





    </View>
  );
};

export default ProfileScreen;

const styles= StyleSheet.create({
  container: {
      flex: 1,
      paddingTop: 5,
      width: "100%",
      backgroundColor: '#e3fcfc',
    },
    body:{
      alignItems:"flex-start",
      justifyContent:'center'
    },

    Title:{
     color:'#000',
     marginBottom:15,
     fontSize: 28,
     fontWeight: 'bold',
     marginTop:10,
     marginHorizontal:15
 },

 BottonTitle:{
  color:'#031169',
  marginBottom:15,
  fontSize: 28,
  fontWeight: 'bold',
  marginTop:10,

},
  detail:{
  flexDirection: 'row',
  width:'100%',   
},
  locationtext:{
     color:'#031169',
     marginBottom:15,
     fontSize: 20,   
     marginLeft:10,
     fontWeight:'bold',

 },
 addresshead:{
  color:'#000',
  marginBottom:15,
  fontSize: 20,   
  marginLeft:10,
  fontWeight:'bold',

},
 addresstext:{
  color:'#031169',
  marginBottom:15,
  fontSize: 20, 

 },
 btn:{

  backgroundColor:'#b6c1c2',
  padding: 6,
  borderRadius: 5,
  alignItems:'center',
  justifyContent: 'center',
  elevation:6,
  margin: 15,
  left: 5,
  bottom: 20,
  width:'15%'

},addicon:{
  width: 35,
  height: 35,

},
 bodytitle:{
  color:'#000',
  marginBottom:15,
  fontSize: 25,
  fontWeight:'bold'  
 },

   HeadCard: {
    borderRadius: 2,
    backgroundColor:'#e3fcfc',
    elevation: 3,
    shadowOffset: { width: 1, height: 1 },
    shadowColor: '#fff',
    shadowOpacity: 0.2,
    shadowRadius: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical:5,
    marginBottom:10,
    marginHorizontal:5
   },
   bodyCard:{
    borderRadius: 2,
    shadowOffset: { width: 1, height: 1 },
    shadowColor: '#fff',
    shadowOpacity: 0.2,
    shadowRadius: 1,
    justifyContent: 'center',
    paddingVertical:5,
    marginBottom:40,
    marginHorizontal:5,


   },
    HospitalImage:{
        marginBottom: 7,
        marginTop: 7,
        alignItems:"center",
        justifyContent:"center",
        width: "90%",
        height: 175
    },

})
