import React,{useState,useEffect} from 'react';
import { StyleSheet,Image, Text, View,Modal,Alert, ScrollView,KeyboardAvoidingView, TouchableOpacity} from 'react-native';
import {TextInput,Button} from 'react-native-paper'
import * as ImagePicker from 'expo-image-picker';
import Feather from 'react-native-vector-icons/Feather';
import * as Permissions from 'expo-permissions';

import MaterialIcons from 'react-native-vector-icons/MaterialIcons';


const EditHospital = ({navigation,route})=>{    

    const {_id,hospitalname,code, email, phone,picture,place,Landmark,District,
        city,state,pincode,EmergencyNo,Emergencydetail,password } = route.params.item
    
    const [Email,setEmail] = useState(email)
    const [Mobile,setPhone] = useState(phone)
    const [Picture,setPicture] = useState(picture)
    const [Emergencycontact,setEmergencyNo] = useState(EmergencyNo)
    const [Emergencyinfo,setEmergencydetail] = useState(Emergencydetail)
    const [Password,setPassword] = useState(password)
    const [secureTextEntry,setSecure] = useState(true)
    const [modal,setModal] = useState(false)
    const [enableshift,setenableShift] = useState(false)    


    useEffect(()=>{
       
        const unsubscribe = navigation.addListener('focus', () => {
           
            setPhone(phone)
            setEmail(email)        
            setPicture(picture)
            setEmergencyNo(EmergencyNo)
            setEmergencydetail(Emergencydetail)
            setPassword(password)
   
            });
            
             
            
            // Return the function to unsubscribe from the event so it gets removed on unmount
            
            return unsubscribe;
      
        },[route.params])



    const UpdateHospital = ()=>{
        fetch("http://mconnecthealth.com:2000/update_Hospital",{
            method:"post",
            headers:{
              'Content-Type': 'application/json'
            },
            body:JSON.stringify({
                id:_id,
                email:Email,
                phone:Mobile,
                picture:Picture,
                EmergencyNo:Emergencycontact,
                Emergencydetail:Emergencyinfo,
                password:Password
            })
        })
        .then(res=>res.json())
        .then(data=>{
            Alert.alert(`${departmentname} is updated successfully`)
            navigation.navigate("Speciality")
        })
        .catch(err=>{
          Alert.alert("something went wrong")
      })
setTimeout(() => {
    setPhone("")
      setEmail("")        
      setPicture("")
      setEmergencyNo("")
      setEmergencydetail("")
      setPassword("")
}, 3000);
      
    }

   const pickFromGallery = async ()=>{
      const {granted} =  await Permissions.askAsync(Permissions.CAMERA_ROLL)
      if(granted){
           let data =  await ImagePicker.launchImageLibraryAsync({
                mediaTypes:ImagePicker.MediaTypeOptions.Images,
                allowsEditing:true,
                aspect:[1,1],
                quality:0.5
            })
            if(!data.cancelled){
                let newfile = { 
                  uri:data.uri,
                  type:`test/${data.uri.split(".")[1]}`,
                  name:`test.${data.uri.split(".")[1]}` 
                  
              }
                handleUpload(newfile)
            }
      }else{
         Alert.alert("you need to give up permission to work")
      }
   }
   const pickFromCamera = async ()=>{
      const {granted} =  await Permissions.askAsync(Permissions.CAMERA)
      if(granted){
           let data =  await ImagePicker.launchCameraAsync({
                mediaTypes:ImagePicker.MediaTypeOptions.Images,
                allowsEditing:true,
                aspect:[1,1],
                quality:0.5
            })
          if(!data.cancelled){
              let newfile = { 
                uri:data.uri,
                type:`test/${data.uri.split(".")[1]}`,
                name:`test.${data.uri.split(".")[1]}` 

            }
              handleUpload(newfile)
          }
      }else{
         Alert.alert("you need to give up permission to work")
      }
   }


   const handleUpload = (image)=>{
        const data = new FormData()
        data.append('file',image)
        data.append('upload_preset','skyMedi')
        data.append("cloud_name","skycloud55")

        fetch("https://api.cloudinary.com/v1_1/skycloud55/image/upload",{
            method:"post",
            body:data
        }).then(res=>res.json()).
        then(data=>{
            setPicture(data.url)
            setModal(false)
        }).catch(err=>{
            Alert.alert("error while uploading")
        })
   }
   

    return(
        <View style={styles.container}>

        <View style={styles.head}> 
        <MaterialIcons 
                        name="navigate-before"
                        size={30}
                        onPress={() => navigation.goBack()}
                        style={styles.back}
                        />
                    
         <Text style={styles.headtext}>Update Hospital Details </Text>
        </View>
        <ScrollView>

            <Text style={styles.Title}>{hospitalname}</Text>
            <View>
            <Image 
            source={{uri:picture}}
            style={styles.HospitalImage}/>
            </View>
     <KeyboardAvoidingView behavior="position" style={styles.root} enabled={enableshift}>
        <View style={styles.formHeader}>
            <TextInput
                label='Email'
                style={styles.inputStyle}
                value={Email}
                onFocus={()=>setenableShift(false)}
                theme={theme}
                mode="outlined"
                onChangeText={text => setEmail(text)}
            />
            <TextInput
                label='Phone'
                style={styles.inputStyle}
                value={Mobile}
                theme={theme}
                onFocus={()=>setenableShift(false)}
                mode="outlined"
                onChangeText={text => setPhone(text)}
            />
            <View style={{flexDirection:'row'}}>
            <TextInput
                label='Password'
                style={styles.inputpassword}
                value={Password}
                secureTextEntry={secureTextEntry ? true : false}
                theme={theme}
                onFocus={()=>setenableShift(false)}
                mode="outlined"
                onChangeText={text => setPassword(text)}
            />
            <TouchableOpacity
                    onPress={()=> setSecure(!secureTextEntry)}
                    style={{marginTop:30}}
                >
                    {secureTextEntry ? 
                    <Feather 
                        name="eye-off"
                        color="grey"
                        size={20}
                    />
                    :
                    <Feather 
                        name="eye"
                        color="grey"
                        size={20}
                    />
                    }
                </TouchableOpacity>
                </View>

            <TextInput
                label='Emergency Contact No'
                style={styles.inputStyle}
                value={Emergencycontact}
                theme={theme}
                onFocus={()=>setenableShift(false)}
                //keyboardType="number-pad"
                mode="outlined"
                onChangeText={text =>setEmergencyNo(text)}
            />
            <TextInput
                label='Emergency Detail'
                style={styles.inputStyle}
                value={Emergencyinfo}
                theme={theme}
                onFocus={()=>setenableShift(false)}
                //keyboardType="number-pad"
                mode="outlined"
                onChangeText={text =>setEmergencydetail(text)}
            />
             
           
             <Button 
             style={styles.uploadImage}
             icon={picture==""?"upload":"check"}
              mode="contained" 
              theme={theme}
              onPress={() => setModal(true)}>
                    Update Image
             </Button>
           
             <Button 
             style={styles.uploadImage}
             icon="content-save"
              mode="contained" 
              theme={theme}
              onPress={() => UpdateHospital()}>
                   Update details
             </Button>
            
     
             
             <Modal
             animationType="slide"
             transparent={true}
             visible={modal}
             onRequestClose={()=>{
                 setModal(false)
             }}
             >
              <View style={styles.modalView}>
                  <View style={styles.modalButtonView}>
                        <Button icon="camera"
                         theme={theme}
                        mode="contained"
                         onPress={() => pickFromCamera()}>
                                camera
                        </Button>
                        <Button 
                        icon="image-area"
                         mode="contained"
                         theme={theme}
                          onPress={() => pickFromGallery()}>
                                gallery
                        </Button>
                  </View>
                <Button 
                 theme={theme}
                onPress={() => setModal(false)}>
                        cancel
                </Button>
              </View>
             </Modal>
         
      </View>
      </KeyboardAvoidingView>
      </ScrollView>
        </View>
 
    )
}

